<script setup>
import { ref } from "vue";

const begin = ref(true);
const end = ref(false);
const huanyuan = ref(false);
const onebutton = ref(false);
const twobutton = ref(false);
const threebutton = ref(false);
const fourbutton = ref(false);
const fivebutton = ref(false);
const sixbutton = ref(false);
const svg1 = ref(true);
const svg2 = ref(false);
const one = ref(false);
const two = ref(false);
const three = ref(false);
const four = ref(false);
const five = ref(false);
const six = ref(false);
const isFlashing1 = ref(false);
const isFlashing2 = ref(false);

// 定义每个按钮的闪烁计数器和间隔
const flashCount1 = ref(0);
const flashCount2 = ref(0);

const flashInterval1 = ref(null);
const flashInterval2 = ref(null);

const startFlashing = (num) => {
  switch (num) {
    case 1:
      flashCount1.value = 0;
      clearInterval(flashInterval1.value);
      flashInterval1.value = setInterval(() => {
        flashCount1.value++;
        if (flashCount1.value === 2) {
          clearInterval(flashInterval1.value);
          isFlashing1.value = false;
        }
      }, 500);
      break;
    case 2:
      flashCount2.value = 0;
      clearInterval(flashInterval2.value);
      flashInterval2.value = setInterval(() => {
        flashCount2.value++;
        if (flashCount2.value === 2) {
          clearInterval(flashInterval2.value);
          isFlashing2.value = false;
        }
      }, 500);
      break;
  }
};

function handlebegin() {
  begin.value = false;
  onebutton.value = true;
}

function handleOne() {
  onebutton.value = false;
  twobutton.value = true;
  one.value = true;
  isFlashing1.value = true;
  startFlashing(1);
}

function handleTwo() {
  twobutton.value = false;
  threebutton.value = true;
  two.value = true;
  isFlashing2.value = true;
  startFlashing(2);
}

function handleThree() {
  threebutton.value = false;
  fourbutton.value = true;
  three.value = true;
}

function handleFour() {
  fourbutton.value = false;
  fivebutton.value = true;
  four.value = true;
  svg2.value = false;
}

function handleFive() {
  fivebutton.value = false;
  sixbutton.value = true;
  five.value = true;
}

function handleSix() {
  sixbutton.value = false;
  end.value = true;
  six.value = true;
}
function handleEnd() {
  svg1.value = false;

  end.value = false;
  huanyuan.value = true;
  svg2.value = true;
}
function handleHuanyuan() {
  huanyuan.value = false;
  begin.value = true;
  onebutton.value = false;
  twobutton.value = false;
  threebutton.value = false;
  fourbutton.value = false;
  fivebutton.value = false;
  sixbutton.value = false;
  svg1.value = true;
  svg2.value = false;
  one.value = false;
  two.value = false;
  three.value = false;
  four.value = false;
  five.value = false;
  six.value = false;
  isFlashing1.value = false;
  isFlashing2.value = false;
  flashCount1.value = 0;
  flashCount2.value = 0;
  clearInterval(flashInterval1.value);
  clearInterval(flashInterval2.value);
}
const isHovered = ref(false);
</script>
