  </template>
  
  <script setup>
  import { onMounted, ref, nextTick } from "vue";
  
  // 定义数据状态
  const one = ref(false);
  const two = ref(false);
  const three = ref(false);
  const adian = ref(false);
  const badian = ref(false);
  const cdian = ref(true);
  const onebutton = ref(true);
  const twobutton = ref(false);
  const threebutton = ref(false);
  const huanyuan = ref(false);
  const isFlashing = ref(false);
  let flashCount = 0;
  let flashInterval = null; 
  
  // 处理点击事件
  const handleOne = () => {
    one.value = true;
    two.value = false;
    three.value = false;
    onebutton.value = false;
    twobutton.value = true;
    adian.value = true;
    isFlashing.value = true;
    startFlashing();
  };
  
  const startFlashing = () => {
    flashCount = 0;
    flashInterval = setInterval(() => {
      flashCount++;
      if (flashCount === 2) {
        clearInterval(flashInterval);
        stopFlashing();
      }
    }, 500);
  };
  
  const stopFlashing = () => {
    isFlashing.value = false;
  };
  
  const handleTwo = () => {
    two.value = true;
    twobutton.value = false;
    threebutton.value = true;
    adian.value = false;
    cdian.value = false;
  };
  
  const handleThree = () => {
    threebutton.value = false;
    three.value = true;
    badian.value = true;
    isFlashing.value = true;
    startFlashing();
    huanyuan.value = true;
  };
  
  const handleHuanyuan = () => {
    one.value = false;
    two.value = false;
    three.value = false;
    adian.value = false;
    badian.value = false;
    onebutton.value = true;
    twobutton.value = false;
    threebutton.value = false;
    huanyuan.value = false;
    cdian.value = true;
  };
  
  </script>
  
  <style scoped lang="scss">
  
  @keyframes abdrawLine {
    from {
      stroke-dashoffset: 423;
    }
    to {
      stroke-dashoffset: 0;
    }
  }
  
  .ab-animation {
    stroke-dasharray: 423;
    stroke-dashoffset: 423;
    animation: abdrawLine 2s linear forwards;
  }
  
  .tb-animation {
    stroke-dasharray: 398;
    stroke-dashoffset: -398;
    animation: tbdrawLine 2s forwards; /* 5秒完成动画 */
  }
  
  @keyframes tbdrawLine {
    from {
      stroke-dashoffset: -398;
    }
    to {
      stroke-dashoffset: 0;
    }
  }
  

  .page {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    gap: 10%;
  }
  
  .container {
    width: 60%;
    display: flex;
    flex-direction: column;
    align-items: center;
    height: 80vh;
    position: relative;
  
    .svg-container {
      width: 100%;
      height: 100%;
      margin: auto;
    }
    p {
      text-align: center;
      font-weight: 800;
    }
  }
  
  .flashing {
    animation: flash 0.5s infinite;
  }
  
  @keyframes flash {
    0%,
    100% {
      opacity: 1;
    }
    50% {
      opacity: 0;
    }
  }
  
  .flashing.stop-flashing {
    animation: none;
    opacity: 1;
  }
  
  button {
    cursor: pointer;
    width: 200px;
    height: 60px;
    background-color: #cdf9c4;
    border: none;
    border-radius: 10px;
  }
  
  button:hover {
    background-color: #b5daae;
  }
  </style>
  